if doc.custom_selling_rate:
    # Check if Item Price with this selling rate already exists
    existing_item_price = frappe.get_all('Item Price',
        filters={
            'item_code': doc.item_code,
            'price_list': 'Standard Selling',  # You can change this to your price list
            'selling': 1,
            'price_list_rate': doc.custom_selling_rate
        }
    )

    # If no such Item Price exists, create a new one
    if not existing_item_price:
        new_item_price = frappe.get_doc({
            'doctype': 'Item Price',
            'item_code': doc.item_code,
            'price_list': 'Standard Selling',  # Specify your price list here
            'price_list_rate': doc.custom_selling_rate,
            'selling': 1
        })

        # Save the new Item Price
        new_item_price.insert()
        frappe.msgprint(f"New Item Price created for item {doc.item_code} with selling rate {doc.custom_selling_rate}")
